    transform: translate(
      0%,
      0%
    ); /* 초기 위치: SVG 요소의 중심이 수직, 수평 중앙에 위치하도록 함 */
  }
  .NUGBUG path {
    animation-name: NUGBUGpath;
    animation-duration: 3s;
    animation-timing-function: ease;
    animation-fill-mode: forwards;
  }
  @keyframes nugbugBox {
    0% {
      background-color: #f4f4f4;
    }
    24% {
      background: none;
    }
    100% {
      background: none;
    }
  }
  @keyframes colorChange {
    from {
      background-color: #fff;
    }

    to {
      background-color: #969696;
    }
  }

  @keyframes nugbug {
    0% {
      transform: scale(1);
    }
    8% {
      transform: scale(0.9);
    }

    100% {
      transform: scale(2);
    }
  }
  @keyframes Team {
